sys_script.script:
/**SNDOC
	@name BR resolveAlert4RTSM
	@description BR on[em_alert] "after update' with condition ci is not null and state changes to "resolved"

	@see https://
	@domain Alert Management
	@author RTSM
	@version 1.0.0
	*/

(function executeRule(current, previous /*null when async*/ ) {
	var log = new RTSMLog(RTSMLog.LOG_DM_IMPACT_CALC, "resolveAlert4RTSM");
	// Only for Dev Test
	var bCalcIntrinsicState = true;

	try {
		log.debug(">>> Start BR resolveAlert4RTSM")

		// TODO: add decision if CI is in RTSM Scope or not and dont calculate the tree
		if (bCalcIntrinsicState) {
			// calculate CI Tree state persistency on receiving resoloved Alert.
			var ciTreeSP = new CITreeSP();
			ciTreeSP.calculateIntrinsicState(current.cmdb_ci);
		}		

		var operation = RTSMConst.QO.resolveAlert;
		var calcCITree = new FunnelFiller();
		var result = calcCITree.insert(operation, current);

		log.info("newAlert4RTSM: CI Tree SP funnel entry result: "+result);
		log.debug("<<< End BR resolveAlert4RTSM")

	} catch (err) {
		log.error("Alert Number - " + current.number + ", error - " + err.message);
		log.debug("Alert Number - " + current.number + ", error - " + err.message);
		log.debug("Stack:" + err.stack);
	}
})(current, previous);
