sys_script.script:
/**SNDOC
	@name BR newAlertRTSM
	@description BR on[em_alert] "after insert' with condition ci is not null and state == open
	Trigger calculation of Intrinsic state of CI Tree state persistency on receiving a new alert and to trigger the impact tree calculation
	@see https://
	@domain Alert Management
	@author RTSM
	@version 1.0.0
	*/

(function executeRule(current, previous /*null when async*/ ) {
	var log = new RTSMLog(RTSMLog.LOG_DM_IMPACT_CALC, "newAlert4RTSM");
	// Only for Dev Test
	var bCalcIntrinsicState = true;

	try {
		log.debug(">>> Start BR newAlert4RTSM");

		// TODO: add decision if CI is in RTSM Scope or not and dont calculate the tree		
		if (bCalcIntrinsicState) {
			// calculate CI Tree state persistency on receiving new Alert.
			var ciTreeSP = new CITreeSP();
			ciTreeSP.calculateIntrinsicState(current.cmdb_ci);
		}

		var operation = RTSMConst.QO.createAlert;
		var calcCITree = new FunnelFiller();
		var result = calcCITree.insert(operation, current);

		log.info("newAlert4RTSM: CI Tree SP funnel entry result: "+result);
		log.debug("<<< End BR newAlert4RTSM");

	} catch (err) {
		log.error("Alert Number - " + current.number + ", error - " + err.message);
		log.debug("Alert Number - " + current.number + ", error - " + err.message);
		log.debug("Stack:" + err.stack);
	}
})(current, previous);
