sys_script_fix.script:
gs.info('RTSM: AfterInstallOrUpgrade START');

		try { 
			var grJob = new GlideRecord('sysauto');
			grJob.addEncodedQuery('sys_scope.nameLIKERTSM^run_type!=once^ORrun_type=NULL');
			grJob.query();
			grJob.setValue('active', true);
			grJob.updateMultiple();

			global.RTSMGlobalScopeUtils.doPostInstallation(); 

		} catch(excep) {
			log.error('[RTSM: AfterInstallOrUpgrade] AfterInstallOrUpgrade failed: ' + excep.message);		
		} 

gs.info('RTSM: AfterInstallOrUpgrade END');
