/**SNDOC
        @name checkOperation
        @description Checks the type of a CI Tree SP Processing Queue Table object. Returns false if the type is not supported.
        @author andreas.reermann@atos.net
        @param  {number} [operation] - Operation from RTSMConst.QO
        @returns {Boolean} 
    */
 RTSMConst.checkOperation = function (op)  {
    if (!op) {
        return false;
    } 
    var n = parseInt(op);
    if (n <= 170 && n >= 10 && (n % 10 == 0)) {
        return true;
    } else {
        return false;
    }
 };

