sys_script_include.script:
/**SNDOC
@domain Utilities
*/
var TableUtil = Class.create();
TableUtil.prototype = {
    initialize: function () {},

    isSublass: function (clazz, generalClass) {
        var arrayUtil = new global.ArrayUtil();
        hier = this.generalization(clazz);
        //gs.info('hierarchy: ' + hier +', generalClass: '+ generalClass);
        return arrayUtil.contains(hier, generalClass);
    },

    hierarchy: function (clazz) {
        hier = new GlideTableHierarchy(clazz);
        return hier.getHierarchy();
    },

    generalization: function (clazz) {
        hier = new GlideTableHierarchy(clazz);
        return hier.getTables();
    },

    subclasses: function(clazz) {
        hier = new GlideTableHierarchy(clazz);
        return hier.getAllExtensions();
    },

    type: 'TableUtil'
};
