var SubGraphVertice = Class.create();
SubGraphVertice.prototype = {
    initialize: function(aSpace) {

        this.vertice = {
            id: "my id", // cmdb_ci.sys_id
            idSpace: aSpace,
            clazz: "my class", // 
            name: "my name",   // 
            modelLayer: "my model layer",
            manager: "manager", // ITOM or Bridge            
            state:  { // Source Tables: [x_atpu_rtsm_ci_tree_state_persistency] AND [em_impact_status]
                id: "my state table id ",
                source: "my state table",
                intrinsicState: 5,//default: Ok
                potentialState: 5,//default: Ok
                upstreamState: 5//default: Ok
            },
            props: { }// properties from [cmdb_ci]
        };
        this.info = {
            isService: -1,       // only for ITOM em_graph.is_service: 1=Service, 0=no Service
            isCluster: -1,      // only for Bridge x_atpu_rtsm_ci_tree_state_persistency.is_cluster: 1=Cluster, 0=no Cluster
            isRootCI: -1,       // Bridge: = x_atpu_rtsm_ci_tree_state_persistency.is_root_ci, ITOM: = isService
            isConnectorCI: 0   // 1 Connector Class, 0 No Connector Class
        };
    },
    type: 'SubGraphVertice'
};
