sys_script_include.script:
/**SNDOC
	@name var Serializer 
	@domain Processing Queue Manager
	@description 
	*/
var Serializer = Class.create();
Serializer.prototype = {
	/**SNDOC
		@name 
		@description 
		@param {*} [queueType] -  
		@param {*} [dataType] -  
		@param {*} [customerSysId] -  
		*/
	initialize: function (queueType, dataType, customerSysId) {
		this.log = new RTSMLog(RTSMLog.LOG_TYPE_SI, "Serializer");
		this.queueType = queueType;
		this.dataType = dataType;
		this.customer = customerSysId;

		//First Queue data member variable
		this.firstQueueData = '';
		this.error = "";
	},
	/**SNDOC
		@name deleteOldData
		@description 
		*/
	deleteOldData: function () {


		this.log.info('deleteOldData : Entered the function ' + this.queueType + ' ' + this.dataType + ' ' + this.customer);
		new CITreeSPProcessingQueueData(this.queueType, this.dataType, this.customer).deleteAllData();
		this.log.info('deleteOldData : Deleted all data ');
		return true;
	},
	/**SNDOC
		@name getError
		@description 
		*/
	getError: function () {

		return this.error;
	},
	/**SNDOC
		@name getFirstQueueData
		@description 
		*/
	getFirstQueueData: function () {

		return this.firstQueueData;
	},

	type: 'Serializer'
};
