sys_script_include.script:
/**SNDOC
	@name CITreeSPProcessorUpdateCIRelation
	@description 
	@domain Impact Tree Builder
	@author ATF2SEV
	@version 1.0.0
	*/
var CITreeSPProcessorUpdateCIRelation = Class.create();
CITreeSPProcessorUpdateCIRelation.prototype = Object.extendsObject(CITreeSPProcessor, {
	/**SNDOC
		@name initialize
		@description 
		*/
	initialize: function () {
		CITreeSPProcessor.prototype.initialize.call(this);
		this.log = new RTSMLog(RTSMLog.LOG_TYPE_SI, "CITreeSPProcessorUpdateCIRelation");			
	},

	/**SNDOC
		@name process
		@description 
		*/
	process: function () {
		this.log.debug("[process] Start , Queue Entry = " + this.queueEntry.getValue("sys_id"));
		var ciRelObj;
		var factory = new SerializerFactory();
		var ciRelation = new CMDBCIRelation();
		var dataType = new CIRelation().getObjectType();

		try {
			var custSysID = this.queueEntry.getValue("customer");

			//Create a Serializer object using the SerializerFactory
			var serializerObject = factory.createSerializer(RTSMConst.QO.updateCIRelation, dataType, custSysID);

			ciRelObj = serializerObject.deserialize(this.queueEntry.getValue("queue_data"));

			this.log.info("[process] parent sys ID: " + ciRelObj.parent.sys_id + " Child Sys ID: " + ciRelObj.child.sys_id);

			if (ciRelObj === null) {
				this.log.info("[process] Deserialization of Queue Data failed");
				//De-serialization was NOT successful, Set Processing Result
				this.error = "Deserialization of Queue Data (CI Relation update) of queue entry failed.";
				this.setError(this.error);
				this.log.error("[process] " + this.error);
				this.log.info("[process] End");
				return;
			}
			//De-serialization was successful
			ciRelation.lock(ciRelObj);
			var ret = ciRelation.updateCIRelation(ciRelObj);
			ciRelation.unlock();

			if (ret !== true) {
				this.log.info("[process] De-serialization was successful but updataion of CI Relation returned false");

				//Set Processing Result
				this.error = ciRelation.getError();
				this.log.info("[process] Error: " + this.error);
				this.setError(this.error);
				this.result.validLeaves = 0;
				this.result.deadLeaves = 0;
				this.result.uniqueCis = 0;
				this.log.info("[process] End");
				return;
			} else {
				this.log.info("CITreeSPProcessorUpdateCIRelation - [process] cheking in else part the return value: " + ret);
				this.triggerRecalcCI(ciRelObj.child.sys_id, ciRelObj);
				this.log.info("[process] CI is connected upstream to other CI's, a recalculation is triggered");
				//Set Processing Result
				this.result.setOk();
				this.result.validLeaves = 1;
				this.result.deadLeaves = 0;
				this.result.uniqueCis = 0;
			}
		} catch (err) {
			this.error = err.message + "\n" + err.stack;
			this.result.setError(this.error);
		}

		this.log.debug("[process] End");
	},

	type: 'CITreeSPProcessorUpdateCIRelation'
});
