sys_script_include.script:
//TODO: Keep for future use, perhaps in SSP, if we need to know the domain of a company
/**SNDOC
	@name CoreCompany
	@description A Company, provides a domain property
	@domain Configuration
	@author ATF2SEV
	@example var aHW = new HelloWorld()
	@version 1.0.0
	*/

var CoreCompany = Class.create();
CoreCompany.prototype = {
	initialize: function () {
		this.log = new RTSMLog(RTSMLog.LOG_TYPE_SI, "CoreCompany");			
		this.gr = '';
		this.error = '';
	},
	/**SNDOC
		@name load
		@description load the customer from the table

		@param {string} [companySysId] - sys_id of company
		@returns {Boolean} true if success
		*/
	load: function (companySysId) {

		this.log.info('load : Company Sys ID - ' + companySysId);

		this.gr = new GlideRecord('core_company');
		this.gr.addQuery('sys_id', companySysId);
		this.gr.query();

		if (this.gr.next()) {

			this.log.info('load : Company Sys ID - ' + this.gr.sys_id);
			return true;
		} else {
			this.error = "Company with sys_id [" + companySysId + "] could not be loaded from the database";
			return false;
		}
	},
	/**SNDOC
		@name getDomain
		@description get Domain of Company

		@returns {string} sys_id of domain
		*/
	getDomain: function () {

		this.log.info('getDomain : Company Sys ID - ' + this.gr.sys_domain);
		return this.gr.sys_domain;
	},
/**SNDOC
	@name getError
	@description get the errormessage created during record load.

	@returns {string} the error message
	*/
	getError: function () {
		return this.error;
	},


	type: 'CoreCompany'
};
