sys_script_include.script:
/**SNDOC
@name CITreeSPProcessorConnectCIs
@description Process a request in the CI Tree SP Queue of Type "connectCIs"
@domain Impact Tree Builder
@author ATF2SEV
@version 1.0.0
*/
var CITreeSPProcessorConnectCIs = Class.create();
CITreeSPProcessorConnectCIs.prototype = Object.extendsObject(CITreeSPProcessor, {
	/**SNDOC
	@name initialize
	@description 
	*/
	initialize: function () {
		CITreeSPProcessor.prototype.initialize.call(this);

		this.log = new RTSMLog(RTSMLog.LOG_TYPE_SI, "CITreeSPProcessorConnectCIs");				
	},
	/**SNDOC
	@name process
	@description  Process a request in the CI Tree SP Queue of Type "connectCIs"
	*/
	process: function () {
		this.log.debug("[process] Start");
		var ciRelObj;
		var factory = new SerializerFactory();
		var cmdbCiRelation = new CMDBCIRelation();
		var dataType = new CIRelation().getObjectType();

		try {
			var custSysID = this.queueEntry.getValue("customer");

			//Create a Serializer object using the SerializerFactory
			var serializerObject = factory.createSerializer(RTSMConst.QO.connectCIs, dataType, custSysID);
			ciRelObj = serializerObject.deserialize(this.queueEntry.getValue("queue_data"));

			if (ciRelObj === null) {
				//De-serialization was NOT successful, Set Processing Result to error
				this.error = "Deserialization of Queue Data (" + this.queueEntry.getValue("queue_data") + ") of queue entry [" + this.queueEntry.getValue("sys_id") + "] failed.";
				this.setError(this.error);
				this.log.debug("[process] End Error= " + this.error);
				return;
			}
			this.log.info("[process] De-serialization was successful");
			//De-serialization was successful
			cmdbCiRelation.lock(ciRelObj);
			var ret = cmdbCiRelation.addCIRelation(ciRelObj);
			cmdbCiRelation.unlock();

			if (ret === true) {
				//CI Relation was added to the model
				this.log.info("[process] CI Relation was added to the model");

				this.triggerRecalcCI(ciRelObj.child.sys_id, ciRelObj);
				this.result.setOk();
				this.result.validLeaves = 1;
				this.result.deadLeaves = 0;
				this.result.uniqueCis = 0;
				this.log.debug("[process] End");
				return;
			} else if (ret === false) {
				this.log.error("[process] De-serialization was successful but addition of CI Relation, Parent [" + ciRelObj.parent.sys_id + "] and Child [" + ciRelObj.child.sys_id + "] returned false");
				this.result.setOk();
				this.result.validLeaves = 0;
				this.result.deadLeaves = 0;
				this.result.uniqueCis = 0;
				this.log.debug("[process] End");
				return;
			} else {
				// Set Processing result to error
				this.error = "Error while processing connectCIs in adding the CI Relation : " + cmdbCiRelation.getError();
				this.setError(this.error);
			}

		} catch (err) {
			this.error = err.message + "\n" + err.stack;
			this.result.setError(this.error);
		}

		this.log.debug("[process] End");
	},

	type: 'CITreeSPProcessorConnectCIs'
});
