sys_ui_action.script:
function _convertDate(date) {
    return "javascript:gs.dateGenerate('" + date.replace(" ","','") + "')";
}

try {
	var grSysEvent = new GlideRecord("sysevent");
	grSysEvent.addQuery("name", "x_atpu_rtsm.citreesp_queue_handler");
	grSysEvent.addQuery("table", "x_atpu_rtsm_citreesp_queue");
	grSysEvent.addQuery("instance", current.sys_id);
	grSysEvent.orderByDesc("sys_created_on");
	grSysEvent.query();

	//TODO Cross Scope Policy Violation
	gs.info('[RTSM UI Action Find Errors]: TODO Cross Scope Policy' + ' # ' + grSysEvent.getRowCount() +  ' /[' + grSysEvent.getTableName() + ']/ ' + grSysEvent.getEncodedQuery());

	if (grSysEvent.next()) {
		gs.addInfoMessage('Open <a href="nav_to.do?uri=' + escape('sysevent.do?sysparm_query=sys_id=' + escape(grSysEvent.sys_id)) + '" target="_blank">' + "related system alert" + '</a>');		

		if (grSysEvent.state != "processed") {
			gs.addErrorMessage("SysEvent not completed"); 
			action.setRedirectURL(current);
		}
		else {
			var addSeconds = grSysEvent.processing_duration / 1000;
			addSeconds = addSeconds.toString().split(".")[0];
			var startDate = new GlideDateTime(grSysEvent.processed);
			var stopDate = new GlideDateTime(grSysEvent.processed);
			stopDate.addSeconds(addSeconds + 1);

			var url = "syslog_list.do?sysparm_query=" + encodeURIComponent(
				   "sys_created_on>=" + _convertDate(startDate.getDisplayValue())
				+ "^sys_created_on<=" + _convertDate(stopDate.getDisplayValue())
				+ "^source=Evaluator^ORsource=com.glide.ui.ServletErrorListener"
				+ "^sys_created_by=" + grSysEvent.sys_created_by
			);

			// gs.addErrorMessage(url); 
			action.setRedirectURL(url);
		}
	}
	else {
		gs.addErrorMessage("Alert not found on table sysevent"); 
	}
}
catch (e) {
	gs.addErrorMessage("error: " + e.message); 
}
