sys_ui_page.client_script:
var custname = [];
var sysIdList = [];
var i=0;
var len=0;

//AJAX Call - to retrieve the list of customers with CI Tree SP enabled.
var ga =  new GlideAjax('AJAX');
ga.addParam('sysparm_name','getCITreeSPEnabledCustomers');
ga.getXML(ParseCITreeSPCustomers);

//AJAX Return Call - returns array of Objects for customers (name,sysId)
function ParseCITreeSPCustomers(response) {
	alert("[ParseCITreeSPCustomers] START");

	var answer = response.responseXML.documentElement.getAttribute('answer');
	alert("[ParseCITreeSPCustomers] answer: " + answer);

	var jsonObj = answer.evalJSON();
	len = jsonObj.length;
	while(i<len) {
		custname.push(jsonObj[i].name);
		sysIdList.push(jsonObj[i].sysID);
		i++;
	}

	jsonStr = JSON.stringify(jsonObj);
	alert("[ParseCITreeSPCustomers] jsonStr: " + jsonStr);

	if(len) {
		//Hide the Overlay if there are no more SP customers to process.
		$('sp_progress').hide();

		var dialogClass = typeof GlideModal != 'undefined' ? GlideModal : GlideDialogWindow;
		dlg = new dialogClass('ProcessCITreeSPRecalculateCustomersPage',true);
		dlg.setTitle("Recalculate CI Tree SP Customers");
		dlg.setPreference("sysparm_spcustomers",custname);
		dlg.setPreference("sysparm_jsonObjArr",jsonStr);
		dlg.setSize(400,800);
		dlg.render();

	}else {
		alert("CI Tree SP is not enabled on the customers. There is nothing to recalculate.");
		reloadWindow(window);
	}
}
