sys_ws_operation.operation_script:
(function process( /*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {

    log = new RTSMLog(null, 'SubGraph-Scripted REST');	
    // var queryParams = request.queryParams;
    var pathParams = request.pathParams;
    var queryParams = request.queryParams;

    log.debug('pathParams: ' + JSON.stringify(pathParams));   
    log.debug('queryParams: ' + JSON.stringify(queryParams));   

	var ctxObjId = null;
	if ('ctxObjId' in pathParams) ctxObjId = pathParams.ctxObjId;	
	log.debug('pathParams ctxObjId: ' + ctxObjId);   

	var idSpace = null;
	if ('idSpace' in pathParams) idSpace = pathParams.idSpace;		
	log.debug('pathParams idSpace: ' + idSpace);   

	var direction = null;
	if ('direction' in queryParams) direction = queryParams.direction[0];			
	log.debug('queryParams direction: ' + direction);   

	var maxSeverity = null;
	if ('maxSeverity' in queryParams) maxSeverity = queryParams.maxSeverity[0];
	log.debug('queryParams maxSeverity: ' + maxSeverity); 

	var maxDepth = null;
	if ('maxDepth' in queryParams) maxDepth = queryParams.maxDepth[0];
	log.debug('queryParams maxDepth: ' + maxDepth); 

	var limit = null;
	if ('limit' in queryParams) limit = queryParams.limit[0];
	log.debug('queryParams limit: ' + limit);  	

	var api = new SubGraphAPI();
    result = api.getSubGraph( ctxObjId, idSpace, direction, maxSeverity, maxDepth, limit);

	log.debug('result.result.code: ' + result.result.code); 

	// https://developer.servicenow.com/dev.do#!/learn/learning-plans/quebec/servicenow_application_developer/app_store_learnv2_rest_quebec_scripted_rest_api_error_objects
	if (result.result.code != SubGraphUtil.OK) {
		var myError = new sn_ws_err.ServiceError();
		if (result.result.code == "missing_param") {
			myError.setStatus(400);
		} else {
			myError.setStatus(500);	
		}
		myError.setMessage(result.result.code + ': ' + result.result.msg);
		myError.setDetail(JSON.stringify(result));
		response.setError(myError);
	} 	

	// 	myError.setStatus(400); // 400 = Bad Request (client Error)
	// 	myError.setStatus(500); // 500 = Internal Server Error (Server Error)

    return result;

})(request, response);
